lines.append('                                <div class="col-span-1"><label class="text-[10px] font-bold uppercase tracking-widest text-gray-400 pl-1 mb-2 block">Qty</label><input type="number" name="qty[]" x-model.number="row.qty" @input="calculateRow(row)" min="0" :max="row.stock" class="w-full h-12 bg-gray-50 rounded-xl px-2 font-bold text-gray-900 border border-transparent hover:border-gray-200 focus:border-green-500/20 focus:bg-white outline-none transition-all text-center text-lg"></div>')
