lines.append('                                <div class="col-span-1"><label class="text-[10px] font-bold uppercase tracking-widest text-gray-400 pl-1 mb-2 block">Total</label><div class="h-12 bg-green-50 rounded-xl px-2 flex items-center justify-center overflow-hidden"><span class="text-sm md:text-base font-bold text-green-600">&#8377;<span x-text="row.total.toFixed(2)"></span></span></div></div>')
lines.append('                            </div>')
