lines.append('            <div class="p-8 pb-4 border-b border-gray-50 hidden xl:block"><h2 class="text-lg font-bold text-gray-900">Bill Summary</h2><p class="text-sm text-gray-400 mt-1">Review before completing</p></div>')
lines.append('            <div class="flex-1 p-8 space-y-6 overflow-y-auto">')
lines.append('                <div class="space-y-4">')
lines.append('                    <div class="flex justify-between items-center py-3 border-b border-gray-50"><span class="text-gray-500">Items</span><span class="font-bold text-gray-900" x-text="rows.length"></span></div>')
lines.append('                    <div class="flex justify-between items-center py-3 border-b border-gray-50"><span class="text-gray-500">Total Quantity</span><span class="font-bold text-gray-900" x-text="totalQty()"></span></div>')
lines.append('                    <div class="flex justify-between items-center py-3 border-b border-gray-50"><span class="text-gray-500">Subtotal</span><span class="font-bold text-gray-900">&#8377;<span x-text="grandTotal()"></span></span></div>')
lines.append('                </div>')
lines.append('                <div class="bg-green-50 rounded-2xl p-6 border border-green-100"><div class="text-xs font-bold uppercase tracking-widest text-green-600 mb-2">Grand Total</div><div class="text-4xl font-bold text-gray-900">&#8377;<span x-text="grandTotal()"></span></div></div>')
lines.append('                <div class="space-y-3">')
lines.append('                    <label class="text-[10px] font-bold uppercase tracking-widest text-gray-400 block">Payment Status</label>')
lines.append('                    <div class="flex bg-gray-100 rounded-xl p-1">')
lines.append('                        <button type="button" @click="paymentStatus = \'UNPAID\'" :class="paymentStatus === \'UNPAID\' ? \'bg-white shadow text-gray-900\' : \'text-gray-500\'" class="flex-1 py-3 rounded-lg font-bold text-sm transition-all">Unpaid</button>')
lines.append('                        <button type="button" @click="paymentStatus = \'PARTIAL\'" :class="paymentStatus === \'PARTIAL\' ? \'bg-white shadow text-gray-900\' : \'text-gray-500\'" class="flex-1 py-3 rounded-lg font-bold text-sm transition-all">Partial</button>')
lines.append('                        <button type="button" @click="paymentStatus = \'PAID\'; amountReceived = parseFloat(grandTotal())" :class="paymentStatus === \'PAID\' ? \'bg-green-500 text-white shadow\' : \'text-gray-500\'" class="flex-1 py-3 rounded-lg font-bold text-sm transition-all">Paid</button>')
lines.append('                    </div>')
lines.append('                    <input type="hidden" name="payment_status" :value="paymentStatus" form="sales-form">')
lines.append('                </div>')
lines.append('                <div x-show="paymentStatus === \'PARTIAL\'" x-transition class="space-y-2">')
lines.append('                    <label class="text-[10px] font-bold uppercase tracking-widest text-gray-400 block">Amount Received</label>')
lines.append('                    <input type="number" step="0.01" name="amount_received" x-model.number="amountReceived" form="sales-form" class="w-full h-14 bg-gray-50 rounded-2xl px-5 font-bold text-gray-900 border border-transparent focus:border-green-500/20 focus:bg-white outline-none transition-all text-center text-xl">')
lines.append('                </div>')
lines.append('            </div>')
lines.append('            <div class="p-8 pt-4 border-t border-gray-50">')
lines.append('                <button type="button" @click="document.getElementById(\'main-submit-btn\').click()" class="bg-green-600 hover:bg-green-700 text-white w-full py-5 rounded-2xl font-bold text-lg shadow-xl shadow-green-600/20 hover:shadow-green-600/30 active:scale-[0.98] transition-all flex items-center justify-center gap-3">Complete Sale</button>')
lines.append('            </div>')
lines.append('        </div>')
lines.append('    </aside>')
lines.append('    <datalist id="product-list"></datalist>')

# Modal
lines.append('    <div x-show="showCustomerModal" class="fixed inset-0 z-[60] flex items-center justify-center bg-black/60 backdrop-blur-sm" style="display: none;" x-transition.opacity>')
lines.append('        <div class="bg-white rounded-3xl p-8 w-full max-w-md shadow-2xl" @click.away="showCustomerModal = false">')
lines.append('            <h3 class="text-2xl font-bold mb-6 text-gray-900">Add New Customer</h3>')
lines.append('            <div class="space-y-4">')
lines.append('                <div><label class="text-xs font-bold text-gray-400 uppercase">Customer Name</label><input type="text" x-model="newCustomerName" class="w-full h-12 px-4 bg-gray-50 border border-gray-200 rounded-xl focus:border-green-500 focus:bg-white outline-none transition-all" placeholder="Enter name"></div>')
lines.append('                <div><label class="text-xs font-bold text-gray-400 uppercase">Mobile Number</label><input type="text" x-model="newCustomerMobile" class="w-full h-12 px-4 bg-gray-50 border border-gray-200 rounded-xl focus:border-green-500 focus:bg-white outline-none transition-all" placeholder="Enter mobile"></div>')
lines.append('                <div><label class="text-xs font-bold text-gray-400 uppercase">City / Village</label><input type="text" x-model="newCustomerCity" class="w-full h-12 px-4 bg-gray-50 border border-gray-200 rounded-xl focus:border-green-500 focus:bg-white outline-none transition-all" placeholder="Enter city or village"></div>')
lines.append('            </div>')
lines.append('            <div class="flex gap-3 mt-8"><button type="button" @click="showCustomerModal = false" class="flex-1 py-3 bg-gray-100 hover:bg-gray-200 font-bold text-gray-600 rounded-xl transition-colors">Cancel</button><button type="button" @click="saveNewCustomer()" class="flex-1 py-3 bg-green-600 hover:bg-green-700 font-bold text-white rounded-xl shadow-lg shadow-green-600/20 transition-all">Save Customer</button></div>')
lines.append('        </div>')
lines.append('    </div>')
lines.append('</div>')

# JS
lines.append('<script>')
lines.append('    function salesForm() {')
# CRITICAL: NO NEWLINES in the Django tags below
lines.append('        const existingItems = {{ existing_items_json|safe|default:"[]" }};')
lines.append('        const existingCustomer = {% if invoice and invoice.customer %}{ id: {{ invoice.customer.id }}, name: "{{ invoice.customer.name }}", city: "{{ invoice.customer.city|default:\'\' }}", mobile_no: "{{ invoice.customer.mobile_no|default:\'\' }}" }{% else %}null{% endif %};')

lines.append('        let initialRows = [];')
lines.append('        if (existingItems.length > 0) {')
lines.append('            initialRows = existingItems.map((item, idx) => ({')
lines.append('                id: Date.now() + idx, productName: item.product_name || "", productId: item.product_id || "", productOpen: false, productResults: [],')
lines.append('                sizes: item.size_label ? [{ size: item.size, unit: item.unit, label: item.size_label }] : [],')
lines.append('                selectedSizeIdx: item.size_label ? 0 : "",')
lines.append('                batches: [{ id: item.batch_id, batch_number: item.batch_number, quantity: item.current_stock, price: item.price, manufacturing_date: item.mfg_date, expiry_date: item.expiry_date }],')
lines.append('                batchId: item.batch_id ? String(item.batch_id) : "", stock: item.current_stock || 0, qty: item.qty || 1, price: item.price || 0, total: item.total || 0, mfgDate: item.mfg_date || "", expDate: item.expiry_date || ""')
lines.append('            }));')
lines.append('        } else {')
lines.append('            initialRows = [{ id: Date.now(), productName: "", productId: "", productOpen: false, productResults: [], sizes: [], selectedSizeIdx: "", batches: [], batchId: "", stock: 0, qty: 1, price: 0, total: 0, mfgDate: "", expDate: "" }];')
lines.append('        }')

lines.append('        return {')
lines.append('            customerSearch: existingCustomer ? `${existingCustomer.name} (${existingCustomer.city}) - ${existingCustomer.mobile_no}` : "",')
lines.append('            customerId: existingCustomer ? existingCustomer.id : "", customerOpen: false, customerResults: [],')
lines.append('            showCustomerModal: false, newCustomerName: "", newCustomerMobile: "", newCustomerCity: "",')
lines.append('            rows: initialRows,')
lines.append("            paymentStatus: {% if invoice %}'{{ invoice.payment_status }}'{% else %}'PAID'{% endif %},")
lines.append("            amountReceived: {% if invoice %}{{ invoice.amount_received|default:0 }}{% else %}0{% endif %},")

lines.append('            async searchCustomers() { if (this.customerSearch.length < 2) { this.customerResults = []; return; } const res = await fetch(`{% url "search_customers" %}?q=${this.customerSearch}`); this.customerResults = await res.json(); this.customerOpen = true; },')
lines.append('            selectCustomer(c) { this.customerId = c.id; this.customerSearch = `${c.name} (${c.city}) - ${c.mobile_no}`; this.customerOpen = false; },')
lines.append('            openCustomerModal() { this.newCustomerName = this.customerSearch; this.showCustomerModal = true; this.customerOpen = false; },')
lines.append('            async saveNewCustomer() { if (!this.newCustomerName || !this.newCustomerMobile) { alert("Name and Mobile are required"); return; } const res = await fetch("{% url "create_customer_ajax" %}", { method: "POST", headers: { "Content-Type": "application/json", "X-CSRFToken": document.querySelector("[name=csrfmiddlewaretoken]").value }, body: JSON.stringify({ name: this.newCustomerName, mobile_no: this.newCustomerMobile, city: this.newCustomerCity }) }); const data = await res.json(); if (data.success) { this.selectCustomer(data); this.showCustomerModal = false; this.newCustomerName = ""; this.newCustomerMobile = ""; this.newCustomerCity = ""; } else { alert(data.error || "Failed"); } },')
lines.append('            async searchProducts(row) { if (row.productName.length < 2) { row.productResults = []; return; } const res = await fetch(`{% url "search_products" %}?q=${row.productName}&format=json`); row.productResults = await res.json(); row.productOpen = true; },')
lines.append('            async selectProduct(row, p) { row.productId = p.id; row.productName = p.name; row.productOpen = false; row.sizes = []; row.selectedSizeIdx = ""; row.batches = []; row.batchId = ""; row.stock = 0; row.price = 0; row.mfgDate = ""; row.expDate = ""; const res = await fetch(`{% url "get_product_sizes" %}?product_id=${p.id}`); row.sizes = await res.json(); },')
lines.append('            async selectSize(row) { row.batches = []; row.batchId = ""; row.stock = 0; row.price = 0; row.mfgDate = ""; row.expDate = ""; if (row.selectedSizeIdx === "") return; const sizeObj = row.sizes[row.selectedSizeIdx]; const res = await fetch(`{% url "get_batches_for_product" %}?product_id=${row.productId}&size=${sizeObj.size}&unit=${sizeObj.unit}`); row.batches = await res.json(); },')
lines.append('            selectBatch(row) { const batch = row.batches.find(b => b.id == row.batchId); if (batch) { row.stock = batch.quantity; row.price = batch.price; row.mfgDate = batch.manufacturing_date || ""; row.expDate = batch.expiry_date || ""; } else { row.stock = 0; row.price = 0; row.mfgDate = ""; row.expDate = ""; } this.calculateRow(row); },')
lines.append('            addRow() { this.rows.push({ id: Date.now(), productName: "", productId: "", productOpen: false, productResults: [], sizes: [], selectedSizeIdx: "", batches: [], batchId: "", stock: 0, qty: 1, price: 0, total: 0 }); },')
lines.append('            removeRow(index) { if (this.rows.length > 1) { this.rows.splice(index, 1); } },')
lines.append('            calculateRow(row) { if (row.qty < 0) row.qty = 0; if (row.stock > 0 && row.qty > row.stock) row.qty = row.stock; row.total = row.qty * row.price; if (this.paymentStatus === "PAID") this.amountReceived = parseFloat(this.grandTotal()); },')
lines.append('            totalQty() { return this.rows.reduce((sum, row) => sum + (row.qty || 0), 0); },')
lines.append('            grandTotal() { return this.rows.reduce((sum, row) => sum + row.total, 0).toFixed(2); }')
lines.append('        }')
lines.append('    }')
lines.append('</script>')
lines.append('{% endblock %}')

with open(file_path, 'w', encoding='utf-8') as f:
    f.write('\n'.join(lines))
    print(f"Successfully wrote {len(lines)} lines to {file_path}")
